import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import cartService from "../services/cartService";
import productService from "../services/productService";
import "./Cart.css";

const Cart = ({ username }) => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [productStocks, setProductStocks] = useState({});
  const [productPrice, setProductPrice] = useState({});
  const [subtotal, setSubtotal] = useState(0);
  const [selectedItems, setSelectedItems] = useState({});
  const [selectedSubtotal, setSelectedSubtotal] = useState(0);

  const navigate = useNavigate();

  useEffect(() => {
    if (!username) {
      navigate("/auth");
    }
  }, [username]);

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await cartService.getCart(username);
        if (response.length > 0 && response[0].cart.items) {
          setCartItems(response[0].cart.items);
          setSubtotal(response[0].subtotal);
          await fetchProductStocks(response[0].cart.items);
          await fetchProductPrice(response[0].cart.items);

          const initialSelectedState = {};
          response[0].cart.items.forEach(
            (item) => (initialSelectedState[item.productId.productID] = false)
          );
          setSelectedItems(initialSelectedState);
        } else {
          setCartItems([]);
        }
      } catch (err) {
        console.error("Error fetching cart:", err);
        setCartItems([]);
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [username]);

  useEffect(() => {
    calculateSelectedSubtotal();
  }, [selectedItems, cartItems, productPrice]);

  const fetchProductStocks = async (items) => {
    const stocks = {};
    await Promise.all(
      items.map(async (item) => {
        try {
          const product = await productService.getProductById(
            item.productId.productID
          );
          stocks[item.productId.productID] =
            product.product.productStorage - product.product.productReservation;
        } catch (err) {
          console.error("Error fetching product stock:", err);
        }
      })
    );
    setProductStocks(stocks);
  };

  const fetchProductPrice = async (items) => {
    const price = {};
    await Promise.all(
      items.map(async (item) => {
        try {
          const product = await productService.getProductById(
            item.productId.productID
          );
          price[item.productId.productID] = product.product.productPrice;
        } catch (err) {
          console.error("Error fetching product price:", err);
        }
      })
    );
    setProductPrice(price);
  };

  const calculateSelectedSubtotal = () => {
    let total = 0;
    cartItems.forEach((item) => {
      if (selectedItems[item.productId.productID]) {
        const price = productPrice[item.productId.productID] || 0;
        total += price * item.quantity;
      }
    });
    setSelectedSubtotal(total);
  };

  const handleRemove = async (productId) => {
    try {
      const updatedCart = await cartService.removeFromCart(username, productId);
      if (updatedCart.items.length === 0) {
        setCartItems([]);
        setSubtotal(0);
        setError(null);
        setSelectedItems({});
      } else {
        setCartItems(updatedCart.items);
        await fetchProductStocks(updatedCart.items);
        await fetchProductPrice(updatedCart.items);
        const response = await cartService.getCart(username);
        setSubtotal(response[0].subtotal);

        const updatedSelectedItems = { ...selectedItems };
        delete updatedSelectedItems[productId];
        setSelectedItems(updatedSelectedItems);
      }
    } catch (err) {
      console.error("Error removing from cart:", err);
      setError(err.message);
    }
  };

  const handleUpdateQuantity = async (productId, quantity) => {
    try {
      const updatedCart = await cartService.updateCart(
        username,
        productId,
        quantity
      );
      setCartItems((prevItems) =>
        prevItems.map((item) =>
          item.productId.productID === productId ? { ...item, quantity } : item
        )
      );
    } catch (error) {
      console.error("Error updating cart:", error);
    }
    const response = await cartService.getCart(username);
    setSubtotal(response[0].subtotal);
  };

  const handleBuyNow = async () => {
    const itemsToCheckout = cartItems.filter(
      (item) => selectedItems[item.productId.productID]
    );

    if (itemsToCheckout.length === 0) {
      setError("Please select at least one item to checkout");
      return;
    }

    let errorMessage = null;
    for (const item of itemsToCheckout) {
      const availableStock = productStocks[item.productId.productID] || 0;
      if (item.quantity > availableStock) {
        errorMessage = `Out of stock for product ${item.productId.productName}. Available: ${availableStock}, Requested: ${item.quantity}`;
        break;
      }
    }

    if (errorMessage) {
      setError(errorMessage);
    } else {
      try {
        navigate("/reservations", { state: { cartItems: itemsToCheckout } });
      } catch (err) {
        setError(err.message);
      }
    }
  };

  const handleSelectAll = (e) => {
    const isChecked = e.target.checked;
    const newSelectedItems = {};
    cartItems.forEach(
      (item) => (newSelectedItems[item.productId.productID] = isChecked)
    );
    setSelectedItems(newSelectedItems);
  };

  const handleSelectItem = (productId, isChecked) => {
    setSelectedItems((prev) => ({
      ...prev,
      [productId]: isChecked,
    }));
  };

  if (loading) return <p>Loading cart...</p>;

  return (
    <div className="cart-page">
      <div className="cart-container">
        <div className="cart-items">
          <header className="cart-header">
            <h1>Your Shopping Cart</h1>
            <h4 className="cart-item">{cartItems.length} Items</h4>
          </header>

          {cartItems.length === 0 ? (
            <div className="empty-cart-message">
              <p>The shopping cart is empty.</p>
            </div>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>
                    <input
                      type="checkbox"
                      onChange={handleSelectAll}
                      checked={
                        cartItems.length > 0 &&
                        cartItems.every(
                          (item) => selectedItems[item.productId.productID]
                        )
                      }
                    />
                  </th>
                  <th>Product</th>
                  <th>Quantity</th>
                  <th>Price</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {cartItems.map((item) => (
                  <tr key={item._id}>
                    <td>
                      <input
                        type="checkbox"
                        checked={
                          selectedItems[item.productId.productID] || false
                        }
                        onChange={(e) =>
                          handleSelectItem(
                            item.productId.productID,
                            e.target.checked
                          )
                        }
                      />
                    </td>
                    <td>{item.productId.productName}</td>
                    <td>
                      <input
                        type="number"
                        min="1"
                        value={item.quantity}
                        onChange={(e) =>
                          handleUpdateQuantity(
                            item.productId.productID,
                            parseInt(e.target.value, 10)
                          )
                        }
                      />
                    </td>
                    <td>
                      $
                      {(
                        (productPrice[item.productId.productID] || 0) *
                        item.quantity
                      ).toFixed(2)}
                    </td>
                    <td>
                      <button
                        onClick={() =>
                          handleRemove(item.productId.productID)
                        }
                      >
                        Remove
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}

          <a href="/" className="back-to-shop">
            ‚Üê Back to Shop
          </a>
        </div>

        <div className="order-summary">
          <h2>Order Summary</h2>
          {cartItems.length > 0 ? (
            <>
              <div className="summary-row">
                <span>Subtotal</span>
                <span>${selectedSubtotal.toFixed(2)}</span>
              </div>
              <div className="summary-row">
                <span>Shipping</span>
                <span>Free</span>
              </div>
              <hr className="order-summary-divider" />
              <div className="summary-row total">
                <span>Total</span>
                <span>${selectedSubtotal.toFixed(2)}</span>
              </div>
              <button onClick={handleBuyNow} className="proceed-checkout-button">
                Checkout Selected Items
              </button>
            </>
          ) : (
            <p className="order-summary-empty">No items to summarize.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Cart;
