// src/components/Order.jsx
import React, { useState, useEffect } from "react";
import orderService from "../services/orderService";
import productService from "../services/productService";
import "./Order.css";

const Order = ({ username }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [allProducts, setAllProducts] = useState([]);
  const [filterInput, setFilterInput] = useState("");
  const [filteredOrders, setFilteredOrders] = useState([]);
  const [sortBy, setSortBy] = useState("Date");
  const [sortOrder, setSortOrder] = useState("Descending");
  const [statusFilter, setStatusFilter] = useState("All");

  useEffect(() => {
    const fetchOrders = async () => {
      setLoading(true);
      try {
        const data = await orderService.getOrdersByUserId(username);
        if (data && Array.isArray(data)) {
          setOrders(data);
          setFilteredOrders(data);
        } else {
          setOrders([]);
          setFilteredOrders([]);
        }
        setError(null);
      } catch (err) {
        setError(err.message || "Failed to fetch orders");
      } finally {
        setLoading(false);
      }
    };

    if (username) {
      fetchOrders();
    }
  }, [username]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const productsData = await productService.getAllProducts();
        if (productsData && Array.isArray(productsData.products)) {
          setAllProducts(productsData.products);
        } else {
          setAllProducts([]);
        }
      } catch (err) {
        console.error("Error fetching products:", err.message);
      }
    };
    fetchProducts();
  }, []);

  const handleFilter = () => {
    const query = filterInput.trim().toLowerCase();

    const filtered = orders.filter((order) => {
      const matchesProductName = order.products.some((product) => {
        const productName =
          product.productId.productName ||
          allProducts.find((p) => p._id === product.productId)?.productName;
        return productName && productName.toLowerCase().startsWith(query);
      });

      return query === "" || matchesProductName;
    });

    const filteredByStatus =
      statusFilter === "All"
        ? filtered
        : filtered.filter((order) =>
            order.products[0].status === (statusFilter === "Received")
          );

    setFilteredOrders(filteredByStatus);
  };

  const handleSort = (event) => {
    const sortOption = event.target.value;
    setSortBy(sortOption);

    const sorted = [...filteredOrders].sort((a, b) => {
      const comparison =
        sortOption === "Price"
          ? b.totalPrice - a.totalPrice
          : new Date(b.createdAt) - new Date(a.createdAt);

      return sortOrder === "Descending" ? comparison : -comparison;
    });

    setFilteredOrders(sorted);
  };

  const toggleSortOrder = () => {
    setSortOrder((prev) => (prev === "Descending" ? "Ascending" : "Descending"));
  };

  const handleStatusFilter = (status) => {
    setStatusFilter(status);
    handleFilter();
  };

  if (loading) return <p className="loading-message">Loading orders...</p>;

  return (
    <div className="order-container">
      <h1 className="order-title">Order History</h1>

      {/* Filters and Sorting Section */}
      <div className="filter-sort-section">
        <div className="filter-section">
          <input
            type="text"
            placeholder="Search by Product Name"
            value={filterInput}
            onChange={(e) => setFilterInput(e.target.value)}
            className="filter-input"
          />
          <button
            onClick={handleFilter}
            className="filter-button"
            style={{ height: "100%" }}
          >
            Search
          </button>
        </div>
        <div className="sort-section">
          <label htmlFor="sort-by" className="sort-label">
            Sort By:
          </label>
          <select
            id="sort-by"
            value={sortBy}
            onChange={handleSort}
            className="sort-select"
          >
            <option value="Date">Order Date</option>
            <option value="Price">Total Price</option>
          </select>
          <button onClick={toggleSortOrder} className="sort-order-button">
            {sortOrder === "Descending" ? "↓ Desc" : "↑ Asc"}
          </button>
        </div>
        <div className="status-filter-section">
          <button
            onClick={() => handleStatusFilter("All")}
            className={`status-filter-button ${
              statusFilter === "All" ? "active" : ""
            }`}
          >
            All
          </button>
          <button
            onClick={() => handleStatusFilter("Shipped")}
            className={`status-filter-button ${
              statusFilter === "Shipped" ? "active" : ""
            }`}
          >
            Shipped
          </button>
          <button
            onClick={() => handleStatusFilter("Received")}
            className={`status-filter-button ${
              statusFilter === "Received" ? "active" : ""
            }`}
          >
            Received
          </button>
        </div>
      </div>

      {/* Error Message */}
      {error && <p className="error-message">{error}</p>}

      {/* Order List */}
      <div className="order-list">
        {filteredOrders.length === 0 ? (
          <p>No orders match your search.</p>
        ) : (
          <table className="order-table">
            <thead>
              <tr>
                <th>Date</th>
                <th>Product</th>
                <th>Total Price</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {filteredOrders.map((order) => (
                <tr key={order.orderId}>
                  <td>{new Date(order.createdAt).toLocaleDateString()}</td>
                  <td>
                    {order.products.map((product) => {
                      const productName =
                        product.productId.productName ||
                        allProducts.find((p) => p._id === product.productId)
                          ?.productName;
                      return (
                        <strong key={product.productId}>
                          {productName} - Quantity: {product.quantity || 1}
                        </strong>
                      );
                    })}
                  </td>
                  <td>${order.totalPrice.toFixed(2)}</td>
                  <td>
                    {order.products[0].status ? (
                      <span className="status-received">Received</span>
                    ) : (
                      <span className="status-shipped">Shipped</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default Order;
