import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./FeaturedBooksSlider.css";

function FeaturedBooksSlider({ featuredBooks }) {
  const [currentIndex, setCurrentIndex] = useState(0); // Start with the first book
  const navigate = useNavigate();

  // Calculate the indices for the left, middle, and right books
  const getVisibleBooks = () => {
    const totalBooks = featuredBooks.length;
    const leftIndex = (currentIndex - 1 + totalBooks) % totalBooks;
    const rightIndex = (currentIndex + 1) % totalBooks;
    return {
      left: featuredBooks[leftIndex],
      middle: featuredBooks[currentIndex],
      right: featuredBooks[rightIndex],
    };
  };

  const handleNext = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % featuredBooks.length);
  };

  const handlePrev = () => {
    setCurrentIndex(
      (prevIndex) => (prevIndex - 1 + featuredBooks.length) % featuredBooks.length
    );
  };

  const { left, middle, right } = getVisibleBooks();

  return (
    <section className="featured-books-slider">
      <h2>Featured Books</h2>
      <div className="slider-container">
        {/* Left Arrow */}
        <button
          className="slider-button prev"
          onClick={handlePrev}
          disabled={featuredBooks.length <= 1}
        >
          &lt;
        </button>

        {/* Slider */}
        <div className="slider">
          {/* Left Book */}
          {left && (
            <div
              className="slider-item left"
              onClick={() => navigate(`/products/${left.productID}`)}
            >
              <img
                src={left.imageURL || "https://via.placeholder.com/150"}
                alt={left.productName}
              />
              <div className="book-details">
                <h3>{left.productName}</h3>
              </div>
            </div>
          )}

          {/* Middle Book (Highlighted) */}
          {middle && (
            <div
              className="slider-item middle"
              onClick={() => navigate(`/products/${middle.productID}`)}
            >
              <img
                src={middle.imageURL || "https://via.placeholder.com/150"}
                alt={middle.productName}
              />
              <div className="book-details">
                <h3>{middle.productName}</h3>
                <p>${middle.productPrice.toFixed(2)}</p>
                <p>{middle.productDescription || "Explore the story of this book."}</p>
              </div>
            </div>
          )}

          {/* Right Book */}
          {right && (
            <div
              className="slider-item right"
              onClick={() => navigate(`/products/${right.productID}`)}
            >
              <img
                src={right.imageURL || "https://via.placeholder.com/150"}
                alt={right.productName}
              />
              <div className="book-details">
                <h3>{right.productName}</h3>
              </div>
            </div>
          )}
        </div>

        {/* Right Arrow */}
        <button
          className="slider-button next"
          onClick={handleNext}
          disabled={featuredBooks.length <= 1}
        >
          &gt;
        </button>
      </div>

      {/* Transparent Line Separator */}
      <div className="line-separator"></div>
    </section>
  );
}

export default FeaturedBooksSlider;
