import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import productService from "../services/productService";
import "./ProductDetail.css";

function ProductDetail() {
  const { _id } = useParams(); // Get the product ID from the URL
  const navigate = useNavigate(); // For navigation
  const [product, setProduct] = useState(null); // State for product details
  const [loading, setLoading] = useState(true); // State for loading
  const [error, setError] = useState(null); // State for errors

  // Fetch product details on component mount
  useEffect(() => {
    async function fetchProduct() {
      setLoading(true);

      try {
        console.log("Fetching product with ID:", _id); // Debugging
        const data = await productService.getProductById(_id);
        console.log("Fetched product details:", data); // Debugging
        setProduct(data); // Set product details in state
      } catch (err) {
        console.error("Error fetching product:", err.message);
        setError(err.message || "Failed to fetch product details");
      } finally {
        setLoading(false);
      }
    }

    fetchProduct();
  }, [_id]);

  // Render loading, error, or product details
  if (loading) return <p>Loading product details...</p>;
  if (error) return <p className="error-message">{error}</p>;
  if (!product) return <p>Product not found</p>;

  return (
    <div className="product-detail-container">
      <button className="back-button" onClick={() => navigate("/")}>
        Back to Home
      </button>
      <div className="product-detail">
        <img
          src={product.productImage || "https://via.placeholder.com/300"}
          alt={product.productName}
          className="product-image"
        />
        <div className="product-info">
          <h1 className="product-name">{product.productName}</h1>
          <p className="product-description">{product.productDescription}</p>
          <p className="product-price">Price: ${product.productPrice?.toFixed(2)}</p>
          <p className="product-stock">
            Available Stock: {product.productStorage - product.productReservation}
          </p>
          <p className="product-featured">
            Featured: {product.featured ? "Yes" : "No"}
          </p>
          <p className="product-id">Product ID: {product.productID}</p>
        </div>
      </div>
    </div>
  );
}

export default ProductDetail;
