import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import productService from "../services/productService";
import reviewService from "../services/reviewService";
import "./Home.css";

function Home() {
  const [books, setBooks] = useState([]);
  const [featuredBooks, setFeaturedBooks] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const navigate = useNavigate();

  // Fetch all products and reviews in parallel
  useEffect(() => {
    async function fetchData() {
      try {
        const productResponse = await productService.getAllProducts();
        if (productResponse.success) {
          const products = productResponse.products;

          // Set all books and filtered books
          setBooks(products);
          setFilteredBooks(products);

          // Filter featured products
          const featured = products.filter((book) => book.featured === true);
          setFeaturedBooks(featured);

          // Fetch reviews only for products with high avgRating
          const bestSellersData = await Promise.all(
            products.map(async (book) => {
              const reviewsResponse = await reviewService.getProductReviews(book.productID);
              const reviews = reviewsResponse.reviews || [];
              const avgRating = reviewsResponse.avgRating || 0;

              const topComment = reviews.reduce(
                (max, review) => (review.Rating > (max?.Rating || 0) ? review : max),
                null
              );

              return {
                ...book,
                avgRating,
                topComment,
              };
            })
          );

          // Sort by avgRating and pick top 5
          const sortedBestSellers = bestSellersData
            .filter((p) => p.avgRating > 0)
            .sort((a, b) => b.avgRating - a.avgRating)
            .slice(0, 5);

          setBestSellers(sortedBestSellers);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    }

    fetchData();
  }, []);

  const handleSearch = () => {
    const filtered = books.filter((book) =>
      book.productName.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredBooks(filtered);
  };

  const handleCategoryFilter = (category) => {
    if (category === "all") {
      setFilteredBooks(books);
    } else {
      setFilteredBooks(books.filter((book) => book.category === category));
    }
  };

  return (
    <div className="home-container">
      {/* Featured Products */}
      <section className="featured-products" style={{ backgroundColor: "#f9f5ff" }}>
        <h2>Featured Products</h2>
        <div className="product-grid">
          {featuredBooks.map((book) => (
            <div
              key={book._id}
              className="product-card"
              onClick={() => navigate(`/products/${book.productID}`)}
            >
              <img
                src={book.productImage}
                alt={book.productName}
                className="product-image"
              />
              <h3>{book.productName}</h3>
              <p>${book.productPrice.toFixed(2)}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Best Sellers */}
      <section className="best-sellers" style={{ backgroundColor: "#fff5e6" }}>
        <h2>Best Sellers</h2>
        <div className="product-grid">
          {bestSellers.map((book) => (
            <div
              key={book._id}
              className="product-card"
              onClick={() => navigate(`/products/${book.productID}`)}
            >
              <img
                src={book.productImage}
                alt={book.productName}
                className="product-image"
              />
              <h3>{book.productName}</h3>
              <p>Average Rating: {book.avgRating.toFixed(1)}</p>
              {book.topComment && (
                <p>Top Comment: "{book.topComment.content}"</p>
              )}
            </div>
          ))}
        </div>
      </section>

      {/* Shop by Category */}
      <section className="categories">
        <h2>Shop by Category</h2>
        <div className="category-buttons">
          {["All", "Fiction", "Non-Fiction", "Science", "History", "Biography"].map(
            (category) => (
              <button
                key={category}
                onClick={() => handleCategoryFilter(category.toLowerCase())}
              >
                {category}
              </button>
            )
          )}
        </div>
        <div className="product-grid">
          {filteredBooks.map((book) => (
            <div
              key={book._id}
              className="product-card"
              onClick={() => navigate(`/products/${book.productID}`)}
            >
              <img
                src={book.productImage}
                alt={book.productName}
                className="product-image"
              />
              <h3>{book.productName}</h3>
              <p>${book.productPrice.toFixed(2)}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Home;
