// src/components/Profile.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../services/authService";
import "./Profile.css";

function Profile({ setIsLoggedIn, setUsername }) {
  const navigate = useNavigate();

  // State to store user information
  const [username, setLocalUsername] = useState(""); // Local username state
  const [email, setEmail] = useState(""); // Local email state

  // State for password reset inputs
  const [otp, setOtp] = useState(""); // For entering OTP
  const [newPassword, setNewPassword] = useState(""); // For entering new password

  // State for response display and error handling
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [statusMessage, setStatusMessage] = useState("");

  // Fetch user profile on component mount
  useEffect(() => {
    async function fetchUserProfile() {
      setLoading(true);
      try {
        const userProfile = await authService.getUserProfile(username); // Pass username if needed
        setLocalUsername(userProfile.username);
        setEmail(userProfile.email);
        setUsername(userProfile.username); // Update global username state
      } catch (err) {
        console.error("Failed to fetch user profile:", err);
        if (err.message === "Unauthorized" || err.message === "Authentication required") {
          setIsLoggedIn(false); // Log the user out if authentication fails
          navigate("/login"); // Redirect to login
        } else {
          setError("Failed to load user profile. Please try again later.");
        }
      } finally {
        setLoading(false);
      }
    }

    fetchUserProfile();
  }, [setIsLoggedIn, setUsername, navigate, username]);

  // Helper to display messages
  const displayMessage = (message) => {
    setStatusMessage(message);
    setLoading(false);
    setError(null);
  };

  // Helper to handle errors
  const handleError = (err) => {
    setError(err.message || "An error occurred");
    setLoading(false);
    setStatusMessage("");
  };

  // Request Password Reset OTP handler
  const handleRequestPasswordResetOTP = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setStatusMessage("");

    try {
      const data = await authService.requestPasswordResetOTP(email); // Use the fetched email
      displayMessage("OTP sent to your email. Please check your inbox.");
      console.log("Request OTP Response:", data);
    } catch (err) {
      handleError(err);
    }
  };

  // Reset Password handler
  const handleResetPassword = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setStatusMessage("");

    // Validate inputs
    if (!otp || !newPassword) {
      handleError({ message: "OTP and new password are required for resetting your password." });
      return;
    }

    try {
      const data = await authService.resetPassword(email, otp, newPassword); // Use the fetched email
      displayMessage("Password reset successfully! You can now log in with your new password.");
      console.log("Reset Password Response:", data);
      // Optionally, log the user out
      handleLogout();
    } catch (err) {
      handleError(err);
    }
  };

  // Logout handler
  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername("");
    navigate("/login");
  };

  if (loading) {
    return <p>Loading user profile...</p>;
  }

  return (
    <div className="profile-container">
      <h1>User Profile</h1>

      {/* User Information */}
      <div className="profile-details">
        <p>
          <strong>Username:</strong> {username}
        </p>
        <p>
          <strong>Email:</strong> {email}
        </p>
      </div>

      {/* Request Password Reset OTP Form */}
      <div>
        <h2>Request Password Reset OTP</h2>
        <form onSubmit={handleRequestPasswordResetOTP}>
          <button type="submit" disabled={loading}>
            {loading ? "Sending OTP..." : "Request OTP"}
          </button>
        </form>
      </div>

      {/* Reset Password Form */}
      <div>
        <h2>Reset Password</h2>
        <form onSubmit={handleResetPassword}>
          <div className="form-group">
            <label>OTP:</label>
            <input
              type="text"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              placeholder="Enter the OTP sent to your email"
              required
            />
          </div>
          <div className="form-group">
            <label>New Password:</label>
            <input
              type="password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              placeholder="Enter your new password"
              required
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? "Resetting Password..." : "Reset Password"}
          </button>
        </form>
      </div>

      {/* Logout Button */}
      <div>
        <button onClick={handleLogout} className="logout-button">
          Log Out
        </button>
      </div>

      {/* Response Display */}
      <div>
        {statusMessage && <p className="status-message success">{statusMessage}</p>}
        {error && <p className="status-message error">{error}</p>}
      </div>
    </div>
  );
}

export default Profile;
