import React, { useEffect, useState} from 'react';
import cartService from '../services/cartService';
import productService from "../services/productService";
import CartItem from './CartItem'; // Import the CartItem component
import { useNavigate } from 'react-router-dom';
import './Cart.css'; // Import CSS for styling

const Cart = ({ username }) => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [productStocks, setProductStocks] = useState({}); // To store product stock information
  const[productPrice, setProductPrice] = useState([]);
  const[subtotal, setSubtotal] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await cartService.getCart(username);
        if (response.length > 0 && response[0].cart.items) {
          setCartItems(response[0].cart.items); // Set cart items from the first element
          setSubtotal(response[0].subtotal);
          await fetchProductStocks(response[0].cart.items); // Fetch product stocks
          await fetchProductPrice(response[0].cart.items);
        } else {
          setCartItems([]); // Set to empty array if items are not present 
        }
      } catch (err) {
        console.error('Error fetching cart:', err);
        setCartItems([]);
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [username]);


  const fetchCart = async () => {
    try {
      const response = await cartService.getCart(username);
      console.log('Cart data:', response); // Log the response data
      if (response.length > 0 && response[0].cart.items) {
        setCartItems(response[0].cart.items); // Set cart items from the first element
        setSubtotal(response[0].subtotal);
        await fetchProductStocks(response[0].cart.items); // Fetch product stocks
        await fetchProductPrice(response[0].cart.items);
      } else {
        setCartItems([]); // Set to empty array if items are not present 
      }
    } catch (err) {
      setCartItems([]);
    } finally {
      setLoading(false);
    }
  };

  const fetchProductStocks = async (items) => {
    const stocks = {};
    await Promise.all(items.map(async (item) => {
      try {
        const product = await productService.getProductById(item.productId.productID);
        stocks[item.productId.productID] = product.product.productStorage - product.product.productReservation;
      } catch (err) {
        console.error('Error fetching product stock:', err);
      }
    }));
    setProductStocks(stocks); // Set the stocks state
  };

  const fetchProductPrice = async (items) => {
    const price = {};
    await Promise.all(items.map(async (item) => {
      try{
        const product = await productService.getProductById(item.productId.productID);
        price[item.productId.productID] = product.product.productPrice;
      } catch (err) {
        console.error("Error fetching product price:", err)
      }
    }));
    setProductPrice(price);
    console.log("2nd", price);
  }

  const handleRemove = async (productId) => {
    console.log('Removing product with ID:', productId);
    try {
      const updatedCart = await cartService.removeFromCart(username, productId);

      // Check if the cart is empty
      if (updatedCart.items.length === 0) {
        setCartItems([]); // Set cart items to empty array
        setSubtotal(0); // Reset subtotal
        setError(null); // Clear any previous errors
      } else {
        setCartItems(updatedCart.items); // Update cart items
        fetchProductStocks(updatedCart.items); // Update stocks after removal
        fetchProductPrice(updatedCart.items);
        const response = await cartService.getCart(username);
        setSubtotal(response[0].subtotal);
        fetchCart();
      }
    } catch (err) {
      console.error('Error removing from cart:', err);
      setError(err.message);
    }
  };


  const handleUpdateQuantity = async (productId, quantity) => {
    try {
      const updatedCart = await cartService.updateCart(username, productId, quantity);
      console.log('Updated Cart from DB:', updatedCart); // Log the entire updated cart

      // Update the cart items in the state
      setCartItems((prevItems) =>
        prevItems.map((item) =>
          item.productId.productID === productId ? { ...item, quantity } : item
        )
      );
    } catch (error) {
      console.error('Error updating cart:', error);
    }
    const response = await cartService.getCart(username);
    setSubtotal(response[0].subtotal);
  };


  const handleBuyNow = async () => {
    let errorMessage = null;

    for (const item of cartItems) {
      const availableStock = productStocks[item.productId.productID] || 0;
      if (item.quantity > availableStock) {
        errorMessage = `Out of stock for product ${item.productId.productName}. Available: ${availableStock}, Requested: ${item.quantity}`;
        break; // Exit loop on first error
      }
    }

    if (errorMessage) {
      setError(errorMessage); // Set the error message
    } else {
      // Proceed with the purchase process
      try {
        navigate('/reservations', { state: { cartItems } });
        console.log("Proceeding to buy items:", cartItems);
        // Add your purchase logic here
      } catch (err) {
        setError(err.message);
      }
    }
  };

  if (loading) return <p>Loading cart...</p>;
  if (cartItems.length == 0) return <p>Your cart is empty.</p>;

  return (
    <div className="cart-container">
      <h1>Your Shopping Cart</h1>
      <table className="cart-table">
        <thead>
          <tr>
            <th>Product</th>
            <th>Quantity</th>
            <th>Available Stock</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {cartItems.map((item) => (
            <CartItem
              key={item._id}
              item={{ ...item, availableStock: productStocks[item.productId.productID], productPrice: productPrice[item.productId.productID] }} // Pass available stock
              onQuantityChange={handleUpdateQuantity} // Pass the quantity change handler
              onRemove={handleRemove}
            />
          ))}
        </tbody>
      </table>
      {error && <p className="error-message">{error}</p>} {/* Display error message */}
      <div className="cart-total">
        <h2>
          Total: ${subtotal}
        </h2>
      </div>
      <button onClick={handleBuyNow}>Buy Now</button>
    </div>
  );
};

export default Cart;
