// src/components/ApiTester.jsx
import React, { useState, useEffect } from 'react';
import authService from '../services/authService';

function ApiTester() {
  // State for form inputs
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [usernameOrEmail, setUsernameOrEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [newPassword, setNewPassword] = useState('');
  
  // State for response display
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  
  // Add this test code to verify API connectivity
  useEffect(() => {
    console.log('Testing API connection...');
    fetch('/api/test')
      .then(response => response.json())
      .then(data => {
        console.log('Test endpoint response:', data);
        // Optionally display the test result in the component
        setResponse({test: data});
      })
      .catch(error => {
        console.error('Test endpoint error:', error);
        // Optionally display the error in the component
        setError('API Test Error: ' + error.message);
      });
  }, []);

  // Helper to display JSON responses
  const displayResponse = (data) => {
    setResponse(data);
    setLoading(false);
    setError(null);
  };

  // Helper to handle errors
  const handleError = (err) => {
    setError(err.message || 'An error occurred');
    setLoading(false);
    setResponse(null);
  };

  // Login handler
  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const data = await authService.login(usernameOrEmail, password);
      displayResponse(data);
    } catch (err) {
      handleError(err);
    }
  };

  // Register handler
  const handleRegister = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const data = await authService.register(username, email, password);
      displayResponse(data);
    } catch (err) {
      handleError(err);
    }
  };

  // Verify email handler
  const handleVerifyEmail = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const data = await authService.verifyEmail(usernameOrEmail, otp);
      displayResponse(data);
    } catch (err) {
      handleError(err);
    }
  };

  // Request password reset OTP handler
  const handleRequestPasswordResetOTP = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const data = await authService.requestPasswordResetOTP(usernameOrEmail);
      displayResponse(data);
    } catch (err) {
      handleError(err);
    }
  };

  // Reset password handler
  const handleResetPassword = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const data = await authService.resetPassword(usernameOrEmail, otp, newPassword);
      displayResponse(data);
    } catch (err) {
      handleError(err);
    }
  };

  // Auth by cookie handler
  const handleAuthByCookie = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const data = await authService.authByCookie();
      displayResponse(data);
    } catch (err) {
      handleError(err);
    }
  };

  // Logout handler
  const handleLogout = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const data = await authService.logout();
      displayResponse(data);
    } catch (err) {
      handleError(err);
    }
  };

  return (
    <div>
      <h1>API Tester</h1>
      
      {/* Test API Endpoint Section */}
      <div>
        <h2>API Test Status</h2>
        <button onClick={() => {
          console.log('Manually testing API connection...');
          fetch('/api/test')
            .then(response => response.json())
            .then(data => {
              console.log('Test endpoint response:', data);
              displayResponse({test: data});
            })
            .catch(error => {
              console.error('Test endpoint error:', error);
              handleError(error);
            });
        }}>
          Test API Connection
        </button>
      </div>
      
      {/* Login Form */}
      <div>
        <h2>Login</h2>
        <form onSubmit={handleLogin}>
          <div>
            <label>Username or Email:</label>
            <input 
              type="text" 
              value={usernameOrEmail} 
              onChange={(e) => setUsernameOrEmail(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label>Password:</label>
            <input 
              type="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? 'Loading...' : 'Login'}
          </button>
        </form>
      </div>

      {/* Rest of your component remains unchanged */}
      {/* Register Form */}
      <div>
        <h2>Register</h2>
        <form onSubmit={handleRegister}>
          <div>
            <label>Username:</label>
            <input 
              type="text" 
              value={username} 
              onChange={(e) => setUsername(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label>Email:</label>
            <input 
              type="email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label>Password:</label>
            <input 
              type="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required 
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? 'Loading...' : 'Register'}
          </button>
        </form>
      </div>

      {/* Verify Email Form */}
      <div>
        <h2>Verify Email</h2>
        <form onSubmit={handleVerifyEmail}>
          <div>
            <label>Username or Email:</label>
            <input 
              type="text" 
              value={usernameOrEmail} 
              onChange={(e) => setUsernameOrEmail(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label>OTP:</label>
            <input 
              type="text" 
              value={otp} 
              onChange={(e) => setOtp(e.target.value)} 
              required 
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? 'Loading...' : 'Verify Email'}
          </button>
        </form>
      </div>

      {/* Request Password Reset OTP Form */}
      <div>
        <h2>Request Password Reset OTP</h2>
        <form onSubmit={handleRequestPasswordResetOTP}>
          <div>
            <label>Username or Email:</label>
            <input 
              type="text" 
              value={usernameOrEmail} 
              onChange={(e) => setUsernameOrEmail(e.target.value)} 
              required 
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? 'Loading...' : 'Request OTP'}
          </button>
        </form>
      </div>

      {/* Reset Password Form */}
      <div>
        <h2>Reset Password</h2>
        <form onSubmit={handleResetPassword}>
          <div>
            <label>Username or Email:</label>
            <input 
              type="text" 
              value={usernameOrEmail} 
              onChange={(e) => setUsernameOrEmail(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label>OTP:</label>
            <input 
              type="text" 
              value={otp} 
              onChange={(e) => setOtp(e.target.value)} 
              required 
            />
          </div>
          <div>
            <label>New Password:</label>
            <input 
              type="password" 
              value={newPassword} 
              onChange={(e) => setNewPassword(e.target.value)} 
              required 
            />
          </div>
          <button type="submit" disabled={loading}>
            {loading ? 'Loading...' : 'Reset Password'}
          </button>
        </form>
      </div>

      {/* Auth by Cookie Button */}
      <div>
        <h2>Auth by Cookie</h2>
        <button onClick={handleAuthByCookie} disabled={loading}>
          {loading ? 'Loading...' : 'Auth by Cookie'}
        </button>
      </div>

      {/* Logout Button */}
      <div>
        <h2>Logout</h2>
        <button onClick={handleLogout} disabled={loading}>
          {loading ? 'Loading...' : 'Logout'}
        </button>
      </div>

      {/* Response Display */}
      <div>
        <h2>Response</h2>
        {error && <div style={{color: 'red'}}>{error}</div>}
        {response && (
          <pre>{JSON.stringify(response, null, 2)}</pre>
        )}
      </div>
    </div>
  );
}

export default ApiTester;
