import React, { useEffect, useState } from 'react';
import productService from './productService'; // Adjust the import based on your file structure
import './ProductManagement.css';

const ProductManagement = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [newProduct, setNewProduct] = useState({
    name: '',
    description: '',
    price: '',
    stock: '',
    imageUrl: ''
  });

  const [editingProductId, setEditingProductId] = useState(null); // Track the product being edited

  useEffect(() => {
    async function fetchProducts() {
      try {
        const data = await productService.getAllProducts();
        setProducts(Array.isArray(data) ? data : data.products || []);
      } catch (err) {
        console.error("Error fetching products:", err);
        setError(err.message || "Failed to fetch products.");
      } finally {
        setLoading(false);
      }
    }
    fetchProducts();
  }, []);

  const handleDelete = async (productId) => {
    try {
      await productService.deleteProduct(productId);
      setProducts(products.filter(product => product.id !== productId));
    } catch (err) {
      console.error("Error deleting product:", err);
      setError(err.message || "Failed to delete product.");
    }
  };

  const handleAddOrUpdateProduct = async (e) => {
    e.preventDefault();
    try {
      if (editingProductId) {
        // Update existing product
        await productService.updateProduct(editingProductId, newProduct);
        setProducts(products.map(product => 
          product.id === editingProductId ? { ...product, ...newProduct } : product
        ));
      } else {
        // Add new product
        const createdProduct = await productService.addProduct(newProduct);
        setProducts([...products, createdProduct]);
      }
      // Reset form
      setNewProduct({ name: '', description: '', price: '', stock: '', imageUrl: '' });
      setEditingProductId(null); // Reset editing state
    } catch (err) {
      console.error("Error saving product:", err);
      setError(err.message || "Failed to save product.");
    }
  };

  const handleEdit = (product) => {
    setNewProduct({
      name: product.name,
      description: product.description,
      price: product.price,
      stock: product.stock,
      imageUrl: product.imageUrl
    });
    setEditingProductId(product.id); // Set the ID of the product being edited
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="product-management-container">
      <h1>Product Management</h1>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Price</th>
            <th>Stock</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.name}</td>
              <td>{product.description}</td>
              <td>{product.price}</td>
              <td>{product.stock}</td>
              <td>
                <button onClick={() => handleEdit(product)}>Edit</button>
                <button onClick={() => handleDelete(product.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="update-form">
        <h2>{editingProductId ? 'Update Product' : 'Add New Product'}</h2>
        <form onSubmit={handleAddOrUpdateProduct}>
          <label>Name:</label>
          <input
            type="text"
            value={newProduct.name}
            onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
            required
          />

          <label>Description:</label>
          <input
            type="text"
            value={newProduct.description}
            onChange={(e) => setNewProduct({ ...newProduct, description: e.target.value })}
            required
          />

          <label>Price:</label>
          <input
            type="number"
            value={newProduct.price}
            onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
            required
          />

          <label>Stock:</label>
          <input
            type="number"
            value={newProduct.stock}
            onChange={(e) => setNewProduct({ ...newProduct, stock: e.target.value })}
            required
          />

          <label>Image URL:</label>
          <input
            type="text"
            value={newProduct.imageUrl}
            onChange={(e) => setNewProduct({ ...newProduct, imageUrl: e.target.value })}
          />

          <button type="submit">{editingProductId ? 'Update Product' : 'Add Product'}</button>
        </form>
      </div>
    </div>
  );
};

export default ProductManagement;
