import React, { useState } from "react";
import "./ContactUs.css";

const ContactUs = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [successMessage, setSuccessMessage] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Simple validation
    if (!formData.name || !formData.email || !formData.message) {
      setErrorMessage("Please fill out all fields.");
      setSuccessMessage("");
      return;
    }

    // Clear form and display success message
    setFormData({ name: "", email: "", message: "" });
    setSuccessMessage("Thank you for reaching out! We'll get back to you soon.");
    setErrorMessage("");
  };

  return (
    <div className="contact-us-container">
      {/* Hero Section */}
      <section className="heros">
        <div className="heros-content">
          <h1>Contact Us</h1>
          <p>
            Have questions or need assistance? We're here to help! Fill out the
            form below or reach out to us directly.
          </p>
        </div>
        <div className="hero-shape">
          <div className="circle-shape"></div>
        </div>
      </section>

      {/* Contact Form */}
      <section className="contact-form-section">
        <h2>Get in Touch</h2>
        <p>
          Please fill out the form below, and our support team will get back to
          you as soon as possible.
        </p>

        <form className="contact-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Full Name</label>
            <input
              type="text"
              id="name"
              name="name"
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter your name"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email Address</label>
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="message">Message</label>
            <textarea
              id="message"
              name="message"
              value={formData.message}
              onChange={handleChange}
              placeholder="Enter your message"
              rows="5"
              required
            ></textarea>
          </div>

          {successMessage && <p className="success-message">{successMessage}</p>}
          {errorMessage && <p className="error-message">{errorMessage}</p>}

          <button type="submit" className="submit-button">
            Submit
          </button>
        </form>
      </section>

      {/* Additional Contact Information */}
      <section className="additional-info-section">
        <h2>Other Ways to Reach Us</h2>
        <p>
          <strong>Email:</strong> support@book.com
        </p>
        <p>
          <strong>Phone:</strong> +1 (555) 123-4567
        </p>
        <p>
          <strong>Address:</strong> 123 Book Street, Knowledge City, USA
        </p>
      </section>

            {/* Footer */}
            <footer class="footer">
        <div class="max-w-6xl mx-auto px-4">
          <div>
            <h4>Company</h4>
            <ul>
              <li><a href="about-us">About Us</a></li>
              <li><a href="privacy-policy">Privacy Policy</a></li>
              <li><a href="return-policy">Return Policy</a></li>
            </ul>
          </div>

          <div>
            <h4>Guides</h4>
            <ul>
              <li><a href="how-to-search">How to Search</a></li>
              <li><a href="making-payment">Making Payment</a></li>
            </ul>
          </div>

          <div>
            <h4>Support</h4>
            <ul>
              <li><a href="FAQ">FAQ</a></li>
              <li><a href="contact-us">Contact Us</a></li>
            </ul>
          </div>
        </div>

        <div class="mt-8">
          © 2025 BOOK.COM. All rights reserved.
        </div>
      </footer>
    </div>
  );
};

export default ContactUs;
