import React, { useEffect, useState } from "react";
import cartService from "../services/cartService";
import productService from "../services/productService";
import { useNavigate } from "react-router-dom";
import "./Cart.css";

const Cart = ({ username }) => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [productStocks, setProductStocks] = useState({});
  const [productPrice, setProductPrice] = useState([]);
  const [subtotal, setSubtotal] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await cartService.getCart(username);
        if (response.length > 0 && response[0].cart.items) {
          setCartItems(response[0].cart.items);
          setSubtotal(response[0].subtotal);
          await fetchProductStocks(response[0].cart.items);
          await fetchProductPrice(response[0].cart.items);
        } else {
          setCartItems([]);
        }
      } catch (err) {
        console.error("Error fetching cart:", err);
        setCartItems([]);
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [username]);

  const fetchProductStocks = async (items) => {
    const stocks = {};
    await Promise.all(
      items.map(async (item) => {
        try {
          const product = await productService.getProductById(
            item.productId.productID
          );
          stocks[item.productId.productID] =
            product.product.productStorage - product.product.productReservation;
        } catch (err) {
          console.error("Error fetching product stock:", err);
        }
      })
    );
    setProductStocks(stocks);
  };

  const fetchProductPrice = async (items) => {
    const price = {};
    await Promise.all(
      items.map(async (item) => {
        try {
          const product = await productService.getProductById(
            item.productId.productID
          );
          price[item.productId.productID] = product.product.productPrice;
        } catch (err) {
          console.error("Error fetching product price:", err);
        }
      })
    );
    setProductPrice(price);
  };

  const handleRemove = async (productId) => {
    try {
      const updatedCart = await cartService.removeFromCart(username, productId);
      if (updatedCart.items.length === 0) {
        setCartItems([]);
        setSubtotal(0);
        setError(null);
      } else {
        setCartItems(updatedCart.items);
        fetchProductStocks(updatedCart.items);
        fetchProductPrice(updatedCart.items);
        const response = await cartService.getCart(username);
        setSubtotal(response[0].subtotal);
      }
    } catch (err) {
      console.error("Error removing from cart:", err);
      setError(err.message);
    }
  };

  const handleUpdateQuantity = async (productId, quantity) => {
    try {
      const updatedCart = await cartService.updateCart(
        username,
        productId,
        quantity
      );
      setCartItems((prevItems) =>
        prevItems.map((item) =>
          item.productId.productID === productId
            ? { ...item, quantity }
            : item
        )
      );
      const response = await cartService.getCart(username);
      setSubtotal(response[0].subtotal);
    } catch (error) {
      console.error("Error updating cart:", error);
    }
  };

  const handleBuyNow = async () => {
    let errorMessage = null;
    for (const item of cartItems) {
      const availableStock = productStocks[item.productId.productID] || 0;
      if (item.quantity > availableStock) {
        errorMessage = `Out of stock for product ${item.productId.productName}. Available: ${availableStock}, Requested: ${item.quantity}`;
        break;
      }
    }

    if (errorMessage) {
      setError(errorMessage);
    } else {
      try {
        navigate("/reservations", { state: { cartItems } });
      } catch (err) {
        setError(err.message);
      }
    }
  };

  if (loading) return <p>Loading cart...</p>;

  return (
    <div className="cart-page">
      <div className="cart-container">
        {/* Shopping Cart Section */}
        <div className="cart-items">
          <header className="cart-header">
            <h1>Your Shopping Cart</h1>
            <span>{cartItems.length} Items</span>
          </header>

          {cartItems.length === 0 ? (
            <div className="empty-cart-message">
              <p>The shopping cart is empty.</p>
            </div>
          ) : (
            cartItems.map((item) => (
              <div className="cart-item" key={item._id}>
                <img
                  src={
                    "https://res.cloudinary.com/doigqstxw/image/upload/v1743099083/careless-people-7_fdqunw.jpg"
                  }
                  alt={item.productId.productName}
                />
                <div className="item-details">
                  <h3>{item.productId.productName}</h3>
                  <p>Ref. {item.productId.productID}</p>
                  <p>${productPrice[item.productId.productID]?.toFixed(2)}</p>
                </div>
                <div className="item-quantity">
                  <button
                    onClick={() =>
                      handleUpdateQuantity(
                        item.productId.productID,
                        item.quantity - 1
                      )
                    }
                    disabled={item.quantity <= 1}
                  >
                    -
                  </button>
                  <span>{item.quantity}</span>
                  <button
                    onClick={() =>
                      handleUpdateQuantity(
                        item.productId.productID,
                        item.quantity + 1
                      )
                    }
                    disabled={
                      item.quantity >= productStocks[item.productId.productID]
                    }
                  >
                    +
                  </button>
                </div>
                <div className="item-total">
                  $
                  {(
                    item.quantity * productPrice[item.productId.productID]
                  ).toFixed(2)}
                </div>
                <button
                  className="remove-item"
                  onClick={() => handleRemove(item.productId.productID)}
                >
                  ×
                </button>
              </div>
            ))
          )}
          <a href="/" className="back-to-shop">
            ← Back to Shop
          </a>
        </div>

        {/* Order Summary Section */}
        <div className="order-summary">
          <h2>Order Summary</h2>
          {cartItems.length > 0 ? (
            <>
              <div className="summary-row">
                <span>Subtotal</span>
                <span>${subtotal.toFixed(2)}</span>
              </div>
              <div className="summary-row">
                <span>Shipping</span>
                <span>Free</span>
              </div>
              <div className="summary-row total">
                <span>Total</span>
                <span>${subtotal.toFixed(2)}</span>
              </div>
              <button
                className="proceed-checkout-button"
                onClick={handleBuyNow}
              >
                Proceed to Checkout
              </button>
            </>
          ) : (
            <p className="order-summary-empty">No items to summarize.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Cart;
