import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./FeaturedBooksSlider.css";

function FeaturedBooksSlider({ featuredBooks }) {
  const [currentIndex, setCurrentIndex] = useState(0); // Start with the first book
  const navigate = useNavigate();

  const handleNext = () => {
    if (currentIndex < featuredBooks.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const handlePrev = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  return (
    <section className="featured-books-slider">
      <h2>Featured Books</h2>
      <div className="slider-container">
        {/* Left Arrow */}
        <button
          className="slider-button prev"
          onClick={handlePrev}
          disabled={currentIndex === 0}
        >
          &lt;
        </button>

        <div className="slider">
          {featuredBooks.map((book, index) => {
            const position = index - currentIndex; // Calculate position relative to the current index
            const isActive = position === 0; // Check if the book is in the center

            return (
              <div
                key={book._id}
                className={`slider-item ${
                  isActive ? "active" : position < 0 ? "left" : "right"
                }`}
                style={{
                  transform: `translateX(${position * 0}%) scale(${
                    isActive ? 1 : 0.75
                  })`,
                  opacity: isActive ? 1 : 0.6,
                  zIndex: isActive ? 2 : 1,
                }}
                onClick={() => isActive && navigate(`/products/${book.productID}`)}
              >
                <img
                  src="https://res.cloudinary.com/doigqstxw/image/upload/v1743099083/careless-people-7_fdqunw.jpg"
                  alt={book.productName}
                  className={`slider-image ${isActive ? "highlighted" : ""}`}
                />
                <div className="book-details">
                  <h3>{book.productName}</h3>
                  {isActive && <p>${book.productPrice.toFixed(2)}</p>}
                  {isActive && (
                    <p>{book.productDescription || "Explore the story of this book."}</p>
                  )}
                </div>
              </div>
            );
          })}
        </div>

        {/* Right Arrow */}
        <button
          className="slider-button next"
          onClick={handleNext}
          disabled={currentIndex === featuredBooks.length - 1}
        >
          &gt;
        </button>
      </div>

      {/* Transparent Line Separator */}
      <div className="line-separator"></div>
    </section>
  );
}

export default FeaturedBooksSlider;
