import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./FeaturedBooksSlider.css";

function FeaturedBooksSlider({ featuredBooks }) {
  const [currentIndex, setCurrentIndex] = useState(2); // Start with the middle book
  const navigate = useNavigate();

  // Calculate the next and previous indices in a circular manner
  const getPrevIndex = () => (currentIndex - 1 + featuredBooks.length) % featuredBooks.length;
  const getNextIndex = () => (currentIndex + 1) % featuredBooks.length;

  const handleNext = () => {
    setCurrentIndex(getNextIndex());
  };

  const handlePrev = () => {
    setCurrentIndex(getPrevIndex());
  };

  return (
    <section className="featured-books-slider">
      <h2>Featured Books</h2>
      <div className="slider-container">
        {/* Left Arrow */}
        <button className="slider-button prev" onClick={handlePrev}>
          &lt;
        </button>

        <div className="slider">
          {featuredBooks.map((book, index) => {
            // Calculate the relative position of each book
            const position =
              (index - currentIndex + featuredBooks.length) % featuredBooks.length;
            const isActive = position === 0; // Check if the book is in the center

            return (
              <div
                key={book._id}
                className={`slider-item ${
                  isActive ? "active" : position < Math.ceil(featuredBooks.length / 2) ? "left" : "right"
                }`}
                style={{
                  transform: `translateX(${(position - Math.floor(featuredBooks.length / 2)) * 120}%) scale(${
                    isActive ? 1 : 0.8
                  })`,
                  opacity: isActive ? 1 : 0.5,
                  zIndex: isActive ? 2 : 1,
                }}
                onClick={() => isActive && navigate(`/products/${book.productID}`)}
              >
                <img
                  src="https://res.cloudinary.com/doigqstxw/image/upload/v1743099083/careless-people-7_fdqunw.jpg"
                  alt={book.productName}
                  className="slider-image"
                />
                {isActive && (
                  <div className="book-details">
                    <h3>{book.productName}</h3>
                    <p>${book.productPrice.toFixed(2)}</p>
                    <p>{book.description || "Explore the story of this book."}</p>
                  </div>
                )}
              </div>
            );
          })}
        </div>

        {/* Right Arrow */}
        <button className="slider-button next" onClick={handleNext}>
          &gt;
        </button>
      </div>

      {/* Transparent Line Separator */}
      <div className="line-separator"></div>
    </section>
  );
}

export default FeaturedBooksSlider;
