import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import productService from "../services/productService";
import "./Home.css";

function Home() {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [genre, setGenre] = useState("");
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    async function fetchBooks() {
      try {
        const response = await productService.getAllProducts();
        if (response.success) {
          setBooks(response.products);
          setFilteredBooks(response.products);
        }
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    }
    fetchBooks();
  }, []);

  useEffect(() => {
    let filtered = [...books];
    if (searchQuery) {
      filtered = filtered.filter((book) =>
        book.productName.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }
    if (genre) {
      filtered = filtered.filter((book) => book.category === genre);
    }
    if (minPrice) {
      filtered = filtered.filter((book) => book.productPrice >= parseFloat(minPrice));
    }
    if (maxPrice) {
      filtered = filtered.filter((book) => book.productPrice <= parseFloat(maxPrice));
    }
    setFilteredBooks(filtered);
  }, [searchQuery, genre, minPrice, maxPrice, books]);

  return (
    <div className="home-container">
      <h1 className="title">Welcome to Our Bookstore</h1>
      <p className="subtitle">
        Immerse yourself in the beauty of books and discover your next great story.
      </p>
      <div className="filters">
        <input
          type="text"
          placeholder="Search by title"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-input"
        />
        <select value={genre} onChange={(e) => setGenre(e.target.value)}>
          <option value="">All Genres</option>
          <option value="fiction">Fiction</option>
          <option value="non-fiction">Non-Fiction</option>
          <option value="fantasy">Fantasy</option>
          <option value="mystery">Mystery</option>
          <option value="science-fiction">Science Fiction</option>
        </select>
        <input
          type="number"
          placeholder="Min Price"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max Price"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
      </div>
      <div className="book-grid">
        {filteredBooks.map((book) => (
          <div
            key={book._id}
            className="book-card"
            onClick={() => navigate(`/products/${book.productID}`)}
          >
            <img
              src={
                book.productImage ||
                "https://res.cloudinary.com/doigqstxw/image/upload/v1743099083/careless-people-7_fdqunw.jpg"
              }
              alt={book.productName}
              className="book-image"
            />
            <h2 className="book-title">{book.productName}</h2>
            <p className="book-author">{book.productDescription}</p>
            <p className="book-price">${book.productPrice.toFixed(2)}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
