// src/components/Order.jsx
import React, { useState, useEffect } from "react";
import orderService from "../services/orderService"; // Service to fetch orders
import productService from "../services/productService"; // Service to fetch products
import "./Order.css";

const Order = ({ username }) => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [allProducts, setAllProducts] = useState([]);
  const [filterId, setFilterId] = useState("");
  const [filteredOrder, setFilteredOrder] = useState(null);
  const [statusMessage, setStatusMessage] = useState("");
  const [currentStatusFilter, setCurrentStatusFilter] = useState("All");

  useEffect(() => {
    const fetchOrders = async () => {
      setLoading(true);
      try {
        const data = await orderService.getOrdersByUserId(username);
        if (data && Array.isArray(data)) {
          setOrders(data);
        } else {
          setOrders([]);
        }
        setError(null);
      } catch (err) {
        setError(err.message || "Failed to fetch orders");
      } finally {
        setLoading(false);
      }
    };

    if (username) {
      fetchOrders();
    }
  }, [username]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const productsData = await productService.getAllProducts();
        if (productsData && Array.isArray(productsData.products)) {
          setAllProducts(productsData.products);
        } else {
          setAllProducts([]);
        }
      } catch (err) {
        console.error("Error fetching products:", err.message);
      }
    };
    fetchProducts();
  }, []);

  const handleFilter = async () => {
    if (!filterId.trim()) {
      setError("Please enter a valid Order ID.");
      return;
    }
    try {
      const order = await orderService.getOrderById(filterId);
      if (order) {
        setFilteredOrder(order);
        setError(null);
      } else {
        setError("Order not found.");
        setFilteredOrder(null);
      }
    } catch (err) {
      setError(err.message || "Failed to fetch order");
      setFilteredOrder(null);
    }
  };

  const handleUpdateStatus = async (orderId) => {
    try {
      const updatedOrder = await orderService.updateOrderStatus(orderId, true);
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order.orderId === updatedOrder.orderId ? updatedOrder : order
        )
      );
      if (filteredOrder && filteredOrder.orderId === updatedOrder.orderId) {
        setFilteredOrder(updatedOrder);
      }
      setStatusMessage("Order status updated successfully!");
    } catch (err) {
      setError(err.message || "Failed to update order status");
    }
  };

  const toggleStatusFilter = () => {
    setCurrentStatusFilter((prev) =>
      prev === "Received" ? "Shipping" : "Received"
    );
  };

  const closePopup = () => {
    setError(null);
  };

  if (loading) return <p className="loading-message">Loading orders...</p>;

  return (
    <div className="order-container">
      <h1 className="order-title">Order History</h1>
      <button
        className="status-filter-button"
        onClick={toggleStatusFilter}
      >
        {currentStatusFilter === "Received"
          ? "Show Shipping"
          : "Show Received"}
      </button>
      <div className="filter-section">
        <input
          type="text"
          placeholder="Enter Order ID"
          value={filterId}
          onChange={(e) => setFilterId(e.target.value)}
          className="filter-input"
        />
        <button
          onClick={handleFilter}
          disabled={!filterId.trim()}
          className="filter-button"
        >
          Get Order
        </button>
      </div>
      {error && (
        <div className="error-popup">
          <div className="popup-content">
            <p>{error}</p>
            <button onClick={closePopup}>Close</button>
          </div>
        </div>
      )}
      {filteredOrder ? (
        <div className="filtered-order">
          <h2 className="filtered-order-title">Filtered Order Details</h2>
          <p>Order ID: {filteredOrder.orderId}</p>
          <p>User ID: {filteredOrder.userId}</p>
          <p>Total Price: ${filteredOrder.totalPrice.toFixed(2)}</p>
          <p>
            Shipping Status:{" "}
            {filteredOrder.products[0].status ? "Received" : "Shipping"}
          </p>
          <p>Order Date: {new Date(filteredOrder.createdAt).toLocaleString()}</p>
          <h3>Products:</h3>
          <ul>
            {filteredOrder.products.map((product, index) => {
              const foundProductName =
                product.productId.productName ||
                allProducts.find((p) => p._id === product.productId)?.productName;

              return (
                <li key={`${filteredOrder.orderId}-${index}`}>
                  Product Name: {foundProductName} - Quantity:{" "}
                  {product.quantity || 1}
                  {!product.status && (
                    <button
                      className="update-status-button"
                      onClick={() =>
                        handleUpdateStatus(filteredOrder.orderId)
                      }
                    >
                      Update Status
                    </button>
                  )}
                </li>
              );
            })}
          </ul>
          {statusMessage && <p className="status-message">{statusMessage}</p>}
        </div>
      ) : (
        <div className="order-list">
          {orders
            .filter((order) => {
              if (currentStatusFilter === "All") return true;
              return (
                order.products[0].status ===
                (currentStatusFilter === "Received")
              );
            })
            .map((order) => (
              <div key={order.orderId} className="order-card">
                <h2 className="order-id">Order ID: {order.orderId}</h2>
                <p>User ID: {order.userId}</p>
                <p>Total Price: ${order.totalPrice.toFixed(2)}</p>
                <p>
                  Shipping Status:{" "}
                  {order.products[0].status ? "Received" : "Shipping"}
                </p>
                <p>Order Date: {new Date(order.createdAt).toLocaleString()}</p>
                <h3>Products:</h3>
                <ul>
                  {order.products.map((product, index) => {
                    const foundProductName =
                      product.productId.productName ||
                      allProducts.find((p) => p._id === product.productId)
                        ?.productName;
                    return (
                      <li key={`${order.orderId}-${index}`}>
                        Product Name: {foundProductName} - Quantity:{" "}
                        {product.quantity || 1}
                        {!product.status && (
                          <button
                            className="update-status-button"
                            onClick={() => handleUpdateStatus(order.orderId)}
                          >
                            Update Status
                          </button>
                        )}
                      </li>
                    );
                  })}
                </ul>
              </div>
            ))}
        </div>
      )}
    </div>
  );
};

export default Order;
