import React, { useEffect, useState } from "react";
import cartService from "../services/cartService";
import productService from "../services/productService";
import CartItem from "./CartItem"; // Import the CartItem component
import { useNavigate } from "react-router-dom";
import "./Cart.css"; // Import existing styles

const Cart = ({ username }) => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [productStocks, setProductStocks] = useState({});
  const [productPrice, setProductPrice] = useState([]);
  const [subtotal, setSubtotal] = useState(0);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const response = await cartService.getCart(username);
        if (response.length > 0 && response[0].cart.items) {
          setCartItems(response[0].cart.items);
          setSubtotal(response[0].subtotal);
          await fetchProductStocks(response[0].cart.items);
          await fetchProductPrice(response[0].cart.items);
        } else {
          setCartItems([]);
        }
      } catch (err) {
        console.error("Error fetching cart:", err);
        setCartItems([]);
      } finally {
        setLoading(false);
      }
    };

    fetchCart();
  }, [username]);

  const fetchProductStocks = async (items) => {
    const stocks = {};
    await Promise.all(
      items.map(async (item) => {
        try {
          const product = await productService.getProductById(item.productId.productID);
          stocks[item.productId.productID] =
            product.product.productStorage - product.product.productReservation;
        } catch (err) {
          console.error("Error fetching product stock:", err);
        }
      })
    );
    setProductStocks(stocks);
  };

  const fetchProductPrice = async (items) => {
    const price = {};
    await Promise.all(
      items.map(async (item) => {
        try {
          const product = await productService.getProductById(item.productId.productID);
          price[item.productId.productID] = product.product.productPrice;
        } catch (err) {
          console.error("Error fetching product price:", err);
        }
      })
    );
    setProductPrice(price);
  };

  const handleRemove = async (productId) => {
    try {
      const updatedCart = await cartService.removeFromCart(username, productId);
      if (updatedCart.items.length === 0) {
        setCartItems([]);
        setSubtotal(0);
        setError(null);
      } else {
        setCartItems(updatedCart.items);
        fetchProductStocks(updatedCart.items);
        fetchProductPrice(updatedCart.items);
        const response = await cartService.getCart(username);
        setSubtotal(response[0].subtotal);
      }
    } catch (err) {
      console.error("Error removing from cart:", err);
      setError(err.message);
    }
  };

  const handleUpdateQuantity = async (productId, quantity) => {
    try {
      await cartService.updateCart(username, productId, quantity);
      setCartItems((prevItems) =>
        prevItems.map((item) =>
          item.productId.productID === productId ? { ...item, quantity } : item
        )
      );
      const response = await cartService.getCart(username);
      setSubtotal(response[0].subtotal);
    } catch (error) {
      console.error("Error updating cart:", error);
    }
  };

  const handleBuyNow = async () => {
    let errorMessage = null;
    for (const item of cartItems) {
      const availableStock = productStocks[item.productId.productID] || 0;
      if (item.quantity > availableStock) {
        errorMessage = `Out of stock for product ${item.productId.productName}. Available: ${availableStock}, Requested: ${item.quantity}`;
        break;
      }
    }

    if (errorMessage) {
      setError(errorMessage);
    } else {
      try {
        navigate("/reservations", { state: { cartItems } });
      } catch (err) {
        setError(err.message);
      }
    }
  };

  if (loading) return <p className="loading-message">Loading cart...</p>;
  if (cartItems.length === 0) return <p className="empty-cart-message">Your cart is empty.</p>;

  return (
    <div className="cart-container">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>Your Shopping Cart</h1>
          <p>Review your selected items before proceeding to checkout.</p>
        </div>
        <div className="hero-shape">
          <div className="circle-shape"></div>
        </div>
      </section>

      {/* Cart Table */}
      <div className="cart-table-container">
        <table className="cart-table">
          <thead>
            <tr>
              <th>Product</th>
              <th>Quantity</th>
              <th>Available Stock</th>
              <th>Price</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {cartItems.map((item) => (
              <CartItem
                key={item._id}
                item={{
                  ...item,
                  availableStock: productStocks[item.productId.productID],
                  productPrice: productPrice[item.productId.productID],
                }}
                onQuantityChange={handleUpdateQuantity}
                onRemove={handleRemove}
              />
            ))}
          </tbody>
        </table>
        {error && <p className="error-message">{error}</p>}
      </div>

      {/* Subtotal and Buy Now */}
      <div className="cart-summary">
        <h2>Total: ${subtotal.toFixed(2)}</h2>
        <button className="buy-now-button" onClick={handleBuyNow}>
          Buy Now
        </button>
      </div>
    </div>
  );
};

export default Cart;
