// src/components/Profile.jsx

import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import authService from "../services/authService";
import "./Profile.css";

function Profile({ setIsLoggedIn, setUsername }) {
  const navigate = useNavigate();

  // State to store user information
  const [username, setLocalUsername] = useState(""); // Local username state
  const [email, setEmail] = useState(""); // Local email state

  // State for response display and error handling
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch user profile on component mount
  useEffect(() => {
    async function fetchUserProfile() {
      setLoading(true);
      try {
        const userProfile = await authService.getUserProfile();
        setLocalUsername(userProfile.username);
        setEmail(userProfile.email);
        setUsername(userProfile.username); // Update global username state
      } catch (err) {
        console.error("Failed to fetch user profile:", err);
        if (err.message === "Authentication required" || err.message === "Invalid or expired token") {
          setIsLoggedIn(false); // Log the user out if authentication fails
          navigate("/login"); // Redirect to login
        } else {
          setError("Failed to load user profile. Please try again later.");
        }
      } finally {
        setLoading(false);
      }
    }

    fetchUserProfile();
  }, [setIsLoggedIn, setUsername, navigate]);

  if (loading) {
    return <p>Loading user profile...</p>;
  }

  return (
    <div className="profile-container">
      <h1>User Profile</h1>

      {/* User Information */}
      <div className="profile-details">
        <p>
          <strong>Username:</strong> {username}
        </p>
        <p>
          <strong>Email:</strong> {email}
        </p>
      </div>

      {error && <p className="error-message">{error}</p>}
    </div>
  );
}

export default Profile;
