import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import productService from "../services/productService";
import reviewService from "../services/reviewService";
import "./Home.css";

function Home() {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [featuredBooks, setFeaturedBooks] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const navigate = useNavigate();

  // Fetch all products
  useEffect(() => {
    async function fetchBooks() {
      try {
        const response = await productService.getAllProducts();
        if (response.success) {
          setBooks(response.products);
          setFilteredBooks(response.products);

          // Filter featured products
          const featured = response.products.filter((book) => book.featured === true);
          setFeaturedBooks(featured);
          console.log("Featured Books:", featured);
        }
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    }
    fetchBooks();
  }, []);

  // Fetch best sellers
  useEffect(() => {
    async function fetchBestSellers() {
      try {
        const productsWithRatings = await Promise.all(
          books.map(async (book) => {
            const reviewsResponse = await reviewService.getProductReviews(book.productID); // Use productID here
            const reviews = reviewsResponse.reviews || [];
            const avgRating = reviewsResponse.avgRating || 0;

            // Determine the highest-rated comment
            const highestRatedComment = reviews.reduce(
              (max, review) => (review.Rating > (max?.Rating || 0) ? review : max),
              null
            );

            return {
              ...book,
              avgRating,
              highestComment: highestRatedComment,
            };
          })
        );

        // Sort products by avgRating in descending order and pick the top 5
        const sortedBestSellers = productsWithRatings
          .filter((p) => p.avgRating > 0)
          .sort((a, b) => b.avgRating - a.avgRating)
          .slice(0, 5);

        setBestSellers(sortedBestSellers);
        console.log("Best Sellers:", sortedBestSellers);
      } catch (error) {
        console.error("Error fetching best sellers:", error);
      }
    }

    if (books.length > 0) {
      fetchBestSellers();
    }
  }, [books]);

  const handleSearch = () => {
    const filtered = books.filter((book) =>
      book.productName.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredBooks(filtered);
  };

  const handleCategoryFilter = (category) => {
    if (category === "all") {
      setFilteredBooks(books);
    } else {
      setFilteredBooks(books.filter((book) => book.category === category));
    }
  };

  return (
    <div className="home-container">
      {/* Hero Section */}
      <section className="hero">
        <div className="hero-content">
          <h1>The Store That Feeds Your Mind. Visit Us Today</h1>
          <p>Where you can browse, buy, and read books in minutes.</p>
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search by Title or Author..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <button onClick={handleSearch}>Search</button>
          </div>
        </div>
        <div className="hero-image">
          <img
            src="https://via.placeholder.com/300x300?text=Bookstore+Illustration"
            alt="Bookstore Illustration"
          />
        </div>
      </section>

      {/* Shop by Category */}
      <section className="categories">
        <h2>Shop by Category</h2>
        <div className="category-buttons">
          {["All", "Fiction", "Non-Fiction", "Science", "History", "Biography"].map(
            (category) => (
              <button
                key={category}
                onClick={() => handleCategoryFilter(category.toLowerCase())}
              >
                {category}
              </button>
            )
          )}
        </div>
        <div className="product-grid">
          {filteredBooks.map((book) => (
            <div
              key={book._id}
              className="product-card"
              onClick={() => navigate(`/products/${book.productID}`)}
            >
              <img
                src={book.productImage}
                alt={book.productName}
                className="product-image"
              />
              <h3>{book.productName}</h3>
              <p>${book.productPrice.toFixed(2)}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Featured Products */}
      <section className="featured-products">
        <h2>Featured Products</h2>
        <div className="product-grid">
          {featuredBooks.map((book) => (
            <div
              key={book._id}
              className="product-card"
              onClick={() => navigate(`/products/${book.productID}`)}
            >
              <img
                src={book.productImage}
                alt={book.productName}
                className="product-image"
              />
              <h3>{book.productName}</h3>
              <p>${book.productPrice.toFixed(2)}</p>
            </div>
          ))}
        </div>
      </section>

      {/* Best Sellers */}
      <section className="best-sellers">
        <h2>Best Sellers</h2>
        <div className="product-grid">
          {bestSellers.map((book) => (
            <div
              key={book._id}
              className="product-card"
              onClick={() => navigate(`/products/${book.productID}`)}
            >
              <img
                src={book.productImage}
                alt={book.productName}
                className="product-image"
              />
              <h3>{book.productName}</h3>
              <p>Average Rating: {book.avgRating.toFixed(1)}</p>
              {book.highestComment && (
                <p>Top Comment: "{book.highestComment.content}"</p>
              )}
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-logo">
            <h2>BookWorms</h2>
            <p>Your one-stop shop for books.</p>
          </div>
          <div className="footer-links">
            <h3>Quick Links</h3>
            <ul>
              <li>Home</li>
              <li>Shop</li>
              <li>Contact</li>
              <li>About</li>
            </ul>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default Home;
