import React, { useState } from "react";
import "./Auth.css";

function Auth() {
  const [isSignUp, setIsSignUp] = useState(false);

  const toggleForm = () => {
    setIsSignUp((prevState) => !prevState); // Toggle between sign-in and sign-up modes
  };

  return (
    <div className="auth-container">
      <div className={`form-container ${isSignUp ? "sign-up-mode" : ""}`}>
        {/* Sign In Form */}
        <div className="form sign-in-form">
          <h1>Login</h1>
          <p>Welcome back! Login to access your account.</p>
          <input type="email" placeholder="Email" required />
          <input type="password" placeholder="Password" required />
          <button className="auth-button">Login</button>
          <p>
            Don't have an account?{" "}
            <span className="toggle-link" onClick={toggleForm}>
              Sign Up
            </span>
          </p>
        </div>

        {/* Sign Up Form */}
        <div className="form sign-up-form">
          <h1>Sign Up</h1>
          <p>Join us today! Create your account now.</p>
          <input type="text" placeholder="Username" required />
          <input type="email" placeholder="Email" required />
          <input type="password" placeholder="Password" required />
          <input type="password" placeholder="Confirm Password" required />
          <button className="auth-button">Sign Up</button>
          <p>
            Already have an account?{" "}
            <span className="toggle-link" onClick={toggleForm}>
              Login
            </span>
          </p>
        </div>
      </div>

      {/* Overlay */}
      <div className="overlay">
        <div className="overlay-content">
          <h1>{isSignUp ? "Welcome Back" : "Hello, Friend!"}</h1>
          <p>
            {isSignUp
              ? "Already have an account? Login to stay connected."
              : "Don't have an account? Sign up and join us today!"}
          </p>
          <button className="auth-button toggle-overlay" onClick={toggleForm}>
            {isSignUp ? "Login" : "Sign Up"}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Auth;
